create or replace function bos_ar_ar_getarhistory_013(
p_szcustid bos_dt_szid,
p_szccyid bos_dt_szsmallid,
p_szarclassid bos_dt_szid,
p_dtmstart timestamp,
p_dtmend timestamp,
p_bunclosedonly bit)
returns void as
$body$
begin

if p_bunclosedonly = 1
  then

select
baa.sztrnid,
baa.szdocid,
baa.shrevno,
baa.szarclassid,
baa.dtmdate,
baa.decamount,
baa.decremain,
baa.szdescription,
baa.bclosed,
baa.dtmclosed,
baa.szurl,
baa.szfinvoicestatus,
baa.szinvworkplaceid,
baa.szreftrnid,
baa.szrefdocid,
baa.szfinvoiceusage,
baa.bcash,
baa.dtmdue,
baa.dtmdue_printed,
baa.dtmperiode,
bsc.szchangenote
from
bos_ar_artrans as baa
left join bos_sd_cjrtracking as bsc on bsc.sztrnid = baa.sztrnid and bsc.szdocid = baa.szdocid

where
baa.szcustid = p_szcustid and
baa.szccyid = p_szccyid and
baa.dtmdate >= p_dtmstart and baa.dtmdate < interval '1 day' + p_dtmend
and baa.bclosed = 0
and baa.szarclassid like p_szarclassid

order by
baa.dtmdate, 
baa.sztrnid,
baa.szdocid,
baa.shrevno;

else

select
baa.sztrnid,
baa.szdocid,
baa.shrevno,
baa.szarclassid,
baa.dtmdate,
baa.decamount,
baa.decremain,
baa.szdescription,
baa.bclosed,
baa.dtmclosed,
baa.szurl,
baa.szfinvoicestatus,
baa.szinvworkplaceid,
baa.szreftrnid,
baa.szrefdocid,
baa.szfinvoiceusage,
baa.bcash,
baa.dtmdue,
baa.dtmdue_printed,
baa.dtmperiode,
bsc.szchangenote

from
bos_ar_artrans as baa
left join bos_sd_cjrtracking as bsc on bsc.sztrnid = baa.sztrnid and bsc.szdocid = baa.szdocid

where
baa.szcustid = p_szcustid and
baa.szccyid = p_szccyid and
baa.dtmdate >= p_dtmstart and baa.dtmdate < interval '1 day' + p_dtmend
and baa.szarclassid like p_szarclassid

order by
baa.dtmdate, 
baa.sztrnid,
baa.szdocid,
baa.shrevno;


end if;
end;
$body$
language plpgsql volatile;
