(dtmdate >= p_dtmfrom and dtmdate < interval '1 day' + p_dtmto) and 
bnomorefollowup like p_sznomorefollowup and bvoid = 0 
order by 
szzipcode,
szaddress_1,
szaddress_2;

end;
