returns void as
$body$
begin

select
fdocorr.szfdocorrectionid,
fdocorr.dtmdate,
fdocorr.szdescription,

fdocorr.bresultfrtransfer,
fdocorr.bvoid,
bos_gen_approved.bapproved,
fdocorr.bapplied,
bos_gl_fjournaltrans.szfjournalid as bjournal,
bos_gl_fjournaltrans.bposted,
fdocorr.btprintedcount,
