p_dtmend timestamp)
returns void as
$body$
begin
select 
bos_ar_customer.szcustid,
bos_sd_fdo.szdoid,
	bos_sd_fdo.dtmdelivery,
	bos_sd_fdo.decamount

from 
bos_sd_fdo
inner join bos_ar_customer on 
bos_sd_fdo.szcustid = bos_ar_customer.szcustid

